import React from "react";

import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import data from "./Data";
import dataTwo from "./DataTwo";

export default function App() {
  

  const [list, setList] = React.useState(data);
  const [listTwo, setListTwo] = React.useState(dataTwo);

  const reorder = (list, startIndex, endIndex) => {
    const result = Array.from(list);
    const [removed] = result.splice(startIndex, 1);
    result.splice(endIndex, 0, removed);

    return result;
  };

  const onEnd = (result) => {
    console.log(result);
    if (result.source.droppableId === result.destination.droppableId) {
      // we are dropping the item in the same list
      const updatingList =
        result.destination.droppableId === "12345678" ? list : listTwo;

      setList(
        reorder(updatingList, result.source.index, result.destination.index)
      );
    } else {
      // we are dropping the item in the other list
      // const updatingList = result.destination.droppableId === "12345678" ? list : listTwo
      // write the logic to update your list state for both droppable containers
    }
  };

  return (
    <DragDropContext onDragEnd={onEnd}>
      <div
        style={{
          display: "flex",flex:"4"
        }}
      >
        <div
          style={{
            width: "100%"
          }}
        >
          <Droppable droppableId="12345678">
            {(provided, snapshot) => (
              <div ref={provided.innerRef}>
                {list.map((item, index) => (
                  <Draggable draggableId={item.id} key={item.id} index={index}>
                    {(provided, snapshot) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                      >
                        <div
                          style={{
                            padding: "20px 0",
                            margin: "20px 0",
                            background: "yellow"
                          }}
                        >
                          {item.name}
                        </div>
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </div>
        <div
          style={{
            width: "100%",
            marginLeft: 20
          }}
        >
          <Droppable droppableId="012345678">
            {(provided, snapshot) => (
              <div ref={provided.innerRef}>
                {listTwo.map((item, index) => (
                  <Draggable draggableId={item.id} key={item.id} index={index}>
                    {(provided, snapshot) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                      >
                        <div
                          style={{
                            padding: "20px 0",
                            margin: "20px 0",
                            background: "red"
                          }}
                        >
                          {item.name}
                        </div>
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </div>
      </div>
    </DragDropContext>
  );
}
