import "./App.css";
import { LineChart, PieChart, Pie, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, AreaChart, Area, BarChart, Bar,} from "recharts";
import { useState,useEffect } from "react";
import axios from "axios";
import Histogram from 'react-chart-histogram';
import Chart from "./Chart";
function App() {

const [data, setData] = useState([])
const labels = [ "Jan", "Feb", "March","April", "May", "June", "July","Aug","Sept","Oct","Nov","Dec"]
  const options = { fillColor: '#FFFFFF', strokeColor: '#0000FF' };
useEffect(() => {
    const salary = [];

    axios.get("http://localhost:3003/task").then(response =>{
        console.log("response", response)
        response.data.data.map(item => {
          console.log("item", item)
            salary.push(item.employee_salary);            
        })
        setData(salary)
        console.log("salary", salary)
    }).catch(e => {
        alert(e);
    })
}, [])
  return (
    <>
      <ResponsiveContainer width="100%" aspect={3}>
      <div>
      <Histogram
          xLabels={labels}
          yValues={data}
          width='1000px'
          height='500px'
          options={options}
      />
    </div>
    </ResponsiveContainer>

    </>
  );
}

export default App;
