import React from "react";
import axios from "axios";
import "./Task.css";
import react, { useEffect, useState } from "react";
import TaskEdit from "../../components/modals/TaskAdd";
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";

const Task = () => {
  const [users, setUsers] = useState([]);
  useEffect(() => {
    loadProduct();
  }, []);

  const loadProduct = async () => {
    await axios.get("http://localhost:3003/task").then((res) => {
      setUsers(res.data);
    });
  };

  const DeleteProduct = async (id) => {
    await axios.delete(`http://localhost:3003/task/${id}`);
    loadProduct();
    setAnchorEl(null);
  };
  const [anchorEl, setAnchorEl] = useState(null);
  const open = Boolean(anchorEl);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };

  const [list, setList] = React.useState([]);
  const [listTwo, setListTwo] = React.useState([]);

  const reorder = (list, startIndex, endIndex) => {
    const result = Array.from(list);
    const [removed] = result.splice(startIndex, 1);
    result.splice(endIndex, 0, removed);

    return result;
  };

  const onEnd = (result) => {
    console.log(result);
    if (result.source.droppableId === result.destination.droppableId) {
      // we are dropping the item in the same list
      const updatingList =
        result.destination.droppableId === "12345678" ? list : listTwo;

      setList(
        reorder(updatingList, result.source.index, result.destination.index)
      );
    } else {
      // we are dropping the item in the other list
      // const updatingList = result.destination.droppableId === "12345678" ? list : listTwo
      // write the logic to update your list state for both droppable containers
    }
  };
  return (
	
	<DragDropContext onDragEnd={onEnd}>
	<div
	  style={{
		display: "flex"
	  }}
	>
	  <div
		style={{
		  width: "100%"
		}}
	  >
		<Droppable droppableId="12345678">
		  {(provided, snapshot) => (
			<div ref={provided.innerRef}>
			  {users.map((item, index) => (
				<Draggable draggableId={item.id} key={item.id} index={index}>
				  {(provided, snapshot) => (
					<div
					  ref={provided.innerRef}
					  {...provided.draggableProps}
					  {...provided.dragHandleProps}
					>
					  <div
						style={{
						  padding: "20px 0",
						  margin: "20px 0",
						  background: "yellow"
						}}
					  >
						{item.name}
					  </div>
					</div>
				  )}
				</Draggable>
			  ))}
			  {provided.placeholder}
			</div>
		  )}
		</Droppable>
	  </div>
	  <div
		style={{
		  width: "100%",
		  marginLeft: 20
		}}
	  >
		<Droppable droppableId="012345678">
		  {(provided, snapshot) => (
			<div ref={provided.innerRef}>
			  {listTwo.map((item, index) => (
				<Draggable draggableId={item.id} key={item.id} index={index}>
				  {(provided, snapshot) => (
					<div
					  ref={provided.innerRef}
					  {...provided.draggableProps}
					  {...provided.dragHandleProps}
					>
					  <div
						style={{
						  padding: "20px 0",
						  margin: "20px 0",
						  background: "red"
						}}
					  >
						{item.name}
					  </div>
					</div>
				  )}
				</Draggable>
			  ))}
			  {provided.placeholder}
			</div>
		  )}
		</Droppable>
	  </div>
	</div>
  </DragDropContext>
  );
};

export default Task;
