import React, { useState } from "react";
import axios from "axios";
import './Login.css'
const Login = ({ handleLogin }) => {
  const [data, setData] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    const value = e.target.value;
    setData({
      ...data,
      [e.target.name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const userData = {
      email: data.email,
      password: data.password,
    };
    axios
      .post("https://reqres.in/api/login", userData)
      .then((response) => {
        console.log(response);
        handleLogin(true);
      })
      .catch((error) => {
        if (error.response) {
          console.log(error.response);
          console.log("server responded");
          alert("wrong");
        }
      });
  };
  return (
    <div className="body">
    <div class="container">
      <main class="signup-container">
        <h1 class="heading-primary">
          Log in<span class="span-blue">.</span>
        </h1>
        <p class="text-mute">Enter your credentials to access your account.</p>
        <form class="signup-form">
          <label class="inp">
            <input
              type="email"
              defaultValue="null"
              name="email"
              value={data.email}
              onChange={handleChange}
              required
              class="input-text"
              placeholder="&nbsp;"
            />
            <span class="label">Email</span>
            <span class="input-icon">
              <i class="fa-solid fa-envelope"></i>
            </span>
          </label>
          <label class="inp">
            <input
              name="password"
              value={data.password}
              onChange={handleChange}
              type="password"
              required
              class="input-text"
              placeholder="&nbsp;"
              id="password"
            />
            <span class="label">Password</span>
            <span class="input-icon input-icon-password" data-password>
              <i class="fa-solid fa-eye"></i>
            </span>
          </label>
          <button onClick={handleSubmit} class="btnn btn-login">
            Login
          </button>
        </form>
        <p class="text-mute">
          Not a member? <a href="$">Sign up</a>
        </p>
      </main>
      <div class="welcome-container">
        <img src="bgLogin.jpg" alt="" />
      </div>
    </div>
    </div>
  );
};

export default Login
