import React from 'react'
import axios from "axios";
import './Task.css'
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import react, { useEffect, useState } from "react";
import { Button, ButtonBase, FormLabel, Menu, MenuItem, TextField } from "@material-ui/core";
import { Link } from "react-router-dom/cjs/react-router-dom.min";
const Task = () => {

    const [users, setUsers] = useState([]);
    const [search, setSearch] = useState("");
    useEffect(() => {
      loadProduct();
    }, []);
  
    const loadProduct = async () => {
      await axios.get("http://localhost:3003/user").then((res) => {
        setUsers(res.data);
      });
    };
    const useStyles = makeStyles({
      btn: {
        backgroundColor: "rgb(228, 228, 250)",
        float: "left",
        marginleft: 50,
      },
      table: {
        minWidth: 650
      }
    });
    const classes = useStyles();
    const DeleteProduct = async (id) => {
      await axios.delete(`http://localhost:3003/user/${id}`);
      loadProduct();
      setAnchorEl(null);
    };
    const [anchorEl, setAnchorEl] = useState(null);
    const open = Boolean(anchorEl);
    const handleClick = (event) => {
      setAnchorEl(event.currentTarget);
    };
    const handleClose = () => {
      setAnchorEl(null);
    };
  return (
    
    <section id="content">	
		<main>
			<div class="head-title">
				<div class="left">
					<h1>Dashboard</h1>
					<ul class="breadcrumb">
						<li>
							<a href="#">Dashboard</a>
						</li>
						<li><i class='bx bx-chevron-right' ></i></li>
						<li>
							<a class="active" href="#">Home</a>
						</li>
					</ul>
				</div>
				<a href="#" class="btn-download">
					<i class='bx bxs-cloud-download' ></i>
					<span class="text">Download PDF</span>
				</a>
			</div>

			

			<div class="table-data">
				<div class="order">
					<div class="head">
						<h3>Tasks</h3>
						<i class='bx bx-search' ></i>
						<i class='bx bx-filter' ></i>
					</div>
                
        

          <Table className={classes.table} aria-label="simple table">
            <TableHead>
              <TableRow>
                <TableCell>UserName</TableCell>
                <TableCell align="left">Name</TableCell>
                <TableCell align="left">Phone&nbsp;</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {users.map((row) => (
                <TableRow key={row.id}>
                  <TableCell component="th" scope="row">
                    {row.UserName}
                  </TableCell>
                  <TableCell align="left">{row.FullName}</TableCell>
                  <TableCell align="left">{row.phone}</TableCell>
                  <TableCell align="left">
                  <Button
        id="basic-button"
        aria-controls={open ? 'basic-menu' : undefined}
        aria-haspopup="true"
        aria-expanded={open ? 'true' : undefined}
        onClick={handleClick}
      >
        Actions
      </Button>
      <Menu
        id="basic-menu"
        anchorEl={anchorEl}
        open={open}
        onClose={handleClose}
      >
        <MenuItem><Link class="btn btn-primary mr-2" to={`/user/${row.id}`}>view</Link> </MenuItem>
        <MenuItem onClick={()=>DeleteProduct(row.id)} >Delete</MenuItem>
        <MenuItem ><Link
                    class="btn btn-outline-primary mr-2"
                    to={`/user/${row.id}`}
                  >
                    Edit
                  </Link></MenuItem>

      </Menu>


                  </TableCell>

                </TableRow>
              ))}
            </TableBody>
          </Table>
        
      
				</div>
				<div class="todo">
					<div class="head">
						<h3>Todos</h3>
						<i class='bx bx-plus' ></i>
						<i class='bx bx-filter' ></i>
					</div>
					<ul class="todo-list">
						<li class="completed">
							<p>Todo List</p>
							<i class='bx bx-dots-vertical-rounded' ></i>
						</li>
						<li class="completed">
							<p>Todo List</p>
							<i class='bx bx-dots-vertical-rounded' ></i>
						</li>
						<li class="not-completed">
							<p>Todo List</p>
							<i class='bx bx-dots-vertical-rounded' ></i>
						</li>
						<li class="completed">
							<p>Todo List</p>
							<i class='bx bx-dots-vertical-rounded' ></i>
						</li>
						<li class="not-completed">
							<p>Todo List</p>
							<i class='bx bx-dots-vertical-rounded' ></i>
						</li>
                        <li class="not-completed">
							<p>Todo List</p>
							<i class='bx bx-dots-vertical-rounded' ></i>
						</li>
					</ul>
				</div>
			</div>
		</main>
	</section>
  )
}

export default Task
