import React, { useState, useEffect } from "react";
import axios from "axios";
import { useHistory, useParams } from "react-router-dom";

import { Input, TextareaAutosize } from "@material-ui/core";

import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const User = () => {

  let history = useHistory();
  const { id } = useParams();
  const [user, setUser] = useState({
    tittle: "",
    price: "",
    desc: "",
    img: "",

  });

  const { tittle, price, desc, img } = user;
  const onInputChange = e => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  useEffect(() => {
    loadUser();
  }, []);

  const onSubmit = async e => {
    e.preventDefault();
    await axios.put(`http://localhost:3003/products/${id}`, user);
    toast("edited");
  history.push("/product")

  };

  const loadUser = async () => {
    const result = await axios.get(`http://localhost:3003/products/${id}`);
    setUser(result.data);
  };
  return (
    <div className="newUser">
     <div className="hero">
    <form onSubmit={e => onSubmit(e)} className="edit-form">
        <div className="input-group">
        <label className="label-edit" for="tittle">Tittle</label>
        <Input
              type="text"
              className="edit-input"

              name="tittle"
              value={tittle}
              onChange={e => onInputChange(e)}
            />

        </div>
        <div className="input-group">
        <label className="label-edit" for="tittle">Price</label>
        <Input
              type="text"
              className="edit-input"

              name="price"
              value={price}
              onChange={e => onInputChange(e)}
            />

        </div>
        <div className="input-group">
        <label className="label-edit" for="tittle">Desc</label>
        <TextareaAutosize 
              type="text"
              className="desc"

              name="desc"
              value={desc}
              onChange={e => onInputChange(e)}
            />

        </div>
        <div className="input-group">
        <label className="label-edit" for="tittle">Image</label>
        <Input
              type="text"
              className="edit-input"

              name="img"
              value={img}
              onChange={e => onInputChange(e)}
            />
        <img src={img}/>
        </div>
        <button  className="btn btn-warning btn-block">Update User</button>
        <ToastContainer  />
    </form>

     </div>
    </div>
  );
};

export default User;
