import React from 'react'
import axios from "axios";
import './Task.css'
import react, { useEffect, useState } from "react";
import TaskEdit from '../../components/modals/TaskAdd';
import  {  useCallback } from "react";

import List, { ItemDragging } from "devextreme-react/list";
const Task = () => {

    const [users, setUsers] = useState([]);
    useEffect(() => {
      loadProduct();
    }, []);
  
    const loadProduct = async () => {
      await axios.get("http://localhost:3003/task").then((res) => {
        setUsers(res.data);
		setPlannedTasks(res.data)
      });
    };
 
    const DeleteProduct = async (id) => {
      await axios.delete(`http://localhost:3003/task/${id}`);
      loadProduct();
      setAnchorEl(null);
    };
    const [anchorEl, setAnchorEl] = useState(null);
    const open = Boolean(anchorEl);
    const handleClick = (event) => {
      setAnchorEl(event.currentTarget);
    };
    const handleClose = () => {
      setAnchorEl(null);
    };
	const [plannedTasksState, setPlannedTasks] = useState([]);
	const [doingTasksState, setDoingTasks] = useState([]);
  
	const onDragStart = (e) => {
	  const tasks =
		e.toData === "doingTasks" ? doingTasksState : plannedTasksState;
	  e.itemData = tasks[e.fromIndex];
	};
	const onAdd = (e) => {
	  const tasks =
		e.toData === "doingTasks" ? doingTasksState : plannedTasksState;
	  const setTasks =
		e.toData === "doingTasks" ? setDoingTasks : setPlannedTasks;
	  setTasks([
		...tasks.slice(0, e.toIndex),
		e.itemData,
		...tasks.slice(e.toIndex)
	  ]);
	};
	const onRemove = (e) => {
	  const tasks =
		e.fromData === "doingTasks" ? doingTasksState : plannedTasksState;
	  const setTasks =
		e.fromData === "doingTasks" ? setDoingTasks : setPlannedTasks;
	  setTasks([...tasks.slice(0, e.fromIndex), ...tasks.slice(e.fromIndex + 1)]);
	};
	const onReorder = (e) => {
	  onRemove(e);
	  onAdd(e);
	};
  return (
    
    <div className="widget-container">
      <List items={plannedTasksState} keyExpr="id" repaintChangesOnly={true}>
        <ItemDragging
          allowReordering={true}
          group="tasks"
          data="plannedTasks"
          onDragStart={onDragStart}
          onAdd={onAdd}
          onRemove={onRemove}
        />
      </List>
      <List items={doingTasksState} keyExpr="id" repaintChangesOnly={true}>
        <ItemDragging
          allowReordering={false}
          group="tasks"
          data="doingTasks"
          onDragStart={onDragStart}
          onAdd={onAdd}
          onReoder={onReorder}
          onRemove={onRemove}
        />
      </List>
    </div>
  )
}

export default Task
