import React from 'react'
import axios from "axios";
import './Task.css'
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import react, { useEffect, useState } from "react";
import { Button, ButtonBase, FormLabel, Menu, MenuItem, TextField } from "@material-ui/core";
import { Link } from "react-router-dom/cjs/react-router-dom.min";
import TaskEdit from '../../components/modals/TaskAdd';
import DraggableList from 'react-draggable-lists';
const Task = () => {
// Initial groups to drag between
const groups = ["group1", "group2", "group3", "noDrop"];
// Initial items to be dragged
const initialItems = [
  { id: 1, group: "group1", value: "drag 1" },
  { id: 2, group: "group1", value: "drag 2" },
  { id: 3, group: "group1", value: "drag 3" }
];
// Sets the state of the items.
// Can be used to add items
const [items, setItems] = useState(initialItems);
// Data about a things id, origin, and destination
const [dragData, setDragData] = useState({});
// Are we hovering over the noDrop div?
const [noDrop, setNoDrop] = useState("");

// Add a new item to `items`
const addItem = () => {
  const newItem = {
	id: items.length + 1,
	group: "group1",
	value: `drag ${items.length + 1}`
  };
  const newItems = [...items, newItem];
  setItems(newItems);
};

const reset = () => {
  setItems(initialItems);
};

// onDragStart we setDragData.
// useState instead of e.dataTransfer so we can transfer more data
const handleDragStart = (e, id, group) => {
  setDragData({ id: id, initialGroup: group });
};

// If we enter the noDrop zone the state will be updated
// Used for styling.
const handleDragEnter = (e, group) => {
  if (group === "noDrop") {
	setNoDrop("noDrop");
  }
};

// DND will not work without this.
const handleDragOver = (e) => {
  e.preventDefault();
};

// setNoDrop to nothing to return styling to normal
const handleDragLeave = (e) => {
  setNoDrop("");
};

// 1. makes copy of items (newItems)
// 2. changes category of the item to its new group
// 3. setItem to our NewItems
const changeCategory = (itemId, group) => {
  const newItems = [...items];
  newItems[itemId - 1].group = group;
  setItems([...newItems]);
};

// 1. setNoDrop in case item was dropped in noDrop
// 2. gets the item id
// 3. doesn't allow drop in noDrop
// 4. changeCategory (see above)
const handleDrop = (e, group) => {
  setNoDrop("");
  const selected = dragData.id;
  if (group === "noDrop") {
	console.log("nuh uh");
  } else {
	changeCategory(selected, group);
  }
};

    const [users, setUsers] = useState([]);
    useEffect(() => {
      loadProduct();
    }, []);
  
    const loadProduct = async () => {
      await axios.get("http://localhost:3003/task").then((res) => {
        setUsers(res.data);
      });
    };
 
    const DeleteProduct = async (id) => {
      await axios.delete(`http://localhost:3003/task/${id}`);
      loadProduct();
      setAnchorEl(null);
    };
    const [anchorEl, setAnchorEl] = useState(null);
    const open = Boolean(anchorEl);
    const handleClick = (event) => {
      setAnchorEl(event.currentTarget);
    };
    const handleClose = () => {
      setAnchorEl(null);
    };
  return (
    
	<section id="content">	
	<main>
		<div class="head-title">
			<div class="left">
				<h1>Dashboard</h1>
				<ul class="breadcrumb">
					<li>
						<a href="#">Dashboard</a>
					</li>
					<li><i class='bx bx-chevron-right' ></i></li>
					<li>
						<a class="active" href="#">Home</a>
					</li>
				</ul>
			</div>
			<a href="#" class="btn-download">
				<i class='bx bxs-cloud-download' ></i>
				<span class="text">Download PDF</span>
			</a>
		</div>

		

		<div class="table-data">
			<div class="order">
				
				<div class="todo">
				<div class="head">
					<h3>Task <span style={{fontSize:"14px"}}>(in progress)</span></h3>
					<TaskEdit/>
					<i class='bx bx-filter' ></i>
				</div>
				<ul class="todo-list">
				<DraggableList  rowSize={1}>

				{users.map((row) => (
					<li class="not-completed">
						<p>{row.name}</p>
						<span>{row.time}</span>
					</li>
					))}
					</DraggableList>
				</ul>
			</div>

	  
	
  
			</div>
			<div class="todo">
				<div class="head">
					<h3>Task<span style={{fontSize:"14px"}}>(completed)</span></h3>
					<i class='bx bx-plus' ></i>
					<i class='bx bx-filter' ></i>
				</div>
				<ul class="todo-list">
					<li class="completed">
						<p>Task</p>
						<i class='bx bx-dots-vertical-rounded' ></i>
					</li>
				</ul>
			</div>
		</div>
	</main>
</section>
  )
}

export default Task
